          this.setState({personalList: {0 : {
            name: item, 
            completion: 'started',
            image: data.background_image
            }
          }})
          this.setState({graphSeg:{
              started: 1,
              finish: 0,
              complete: 0
            }})
          this.setState({isThere: true})                
