import React from 'react';

export default function Graph({graphSeg, log}) {
    function createGraph() {
        let amount = graphSeg.started + graphSeg.finish + graphSeg.complete
        let startPerc = (graphSeg.started / amount) * 100
        let completePerc = 100 - ((graphSeg.complete / amount) * 100)

        let circStart = (graphSeg.started / amount) * 360
        let circFIn = ((graphSeg.finish / amount) * 360) + circStart
        // let circComp = (graphSeg.complete / amount) * 360
        createLines()
        // return {background: `conic-gradient(rgb(248, 50, 208) ${circStart}deg, rgb(75, 192, 255) ${circStart}deg ${circFIn} deg,rgb(47, 255, 82) ${circFIn}deg)`}
        return {background: `linear-gradient(to right, rgb(248, 50, 208) ${startPerc}%, rgb(75, 192, 255) ${startPerc}%, rgb(75, 192, 255) ${completePerc}% ,rgb(47, 255, 82) ${completePerc}%)`}
    }

    function createLines() {
       let amount = graphSeg.started + graphSeg.finish + graphSeg.complete
       if(amount > 0) {
        log.map((item) => {
            number = log.indexOf(item)
            return(
                <div className='lines'>
                    <div className='number'>{item + 1}</div>
                </div>
            )
        })
       }
    }        
    


    return(
        <div className='graph-cont'>
            <div 
            className="graph"
            style={createGraph()}
            >
                {createLines()}
            </div>
            
        </div>
    )
}
