import React from 'react';

export default function Main(props) {
    let items = props.items
    
    let listItems = items.map((item) => {
                    let number = items.indexOf(item)
                    console.log(props.pers[number].image);
                    return(
                        <div className='list-item'>
                            <div className='image-container' style={{backgroundImage: `url(${props.pers[number].image})`}}>
                              {/* <img className='game-image' src={props.pers[number].image} alt={item} />   */}
                            </div>
                            

                            <li key={'list' + number} >{item}</li>

                            <div key={'item' + number} className='select-cont'>
                                <div
                                key={1} 
                                onClick={(e) => props.onActive(e, 'one')}
                                className={props.pers[number].completion === 'started' ? 'select red' : 'select'}
                                /> 
                                <div
                                key={2} 
                                onClick={(e) => props.onActive(e, 'two')} 
                                className={props.pers[number].completion === 'finish' ? 'select blue' : 'select'}
                                /> 
                                <div
                                key={3} 
                                onClick={(e) => props.onActive(e, 'three')} 
                                className={props.pers[number].completion === 'complete' ? 'select green' : 'select'}
                                />  
                            </div>                            
                        </div>                    
                    )
                    
                })

    return(
                
            <ul className='list-cont'>
                {listItems.length > 0 ? listItems : []}
            </ul>
        
    )
}
