import React, { Component } from 'react';
import Search from './components/Search';
import Main from './components/Main';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import games from './games'


class App extends Component {
  constructor() {
    super()
    this.state = { 
    games : games,
    search: '',
    log: [],
    active: {
      started: false,
      finish: false,
      complete: false
    },
    personalList: {}
   } 
  }

  onActive = (e, ver) => {

    let name = e.target.parentNode.previousSibling.innerText
    let number = this.state.log.indexOf(name)

    if(ver === 'one') {      
      let current = this.state.personalList[number].active.started
      this.setState(prevState => ({
        personalList: {
          [number] : {
            ...prevState.active,
            started: !current,
            finish: false,
            complete: false
         }
        }
      })
      )
    //   this.setState(prevState => ({
    //   personalList : {
    //     ...prevState.personalList,
    //     [number] : {
    //       name: name,
    //       completion: 'started'
    //     }
    //   }
    // }))

    } else if (ver === 'two') {
        let current = this.state.personalList[number].active.finish
        this.setState(prevState => ({
          personalList: {
            [number] : {
              ...prevState.active,
              started: false,
              finish: !current,
              complete: false
          }
        }
      })
      )
    //   this.setState(prevState => ({
    //   personalList : {
    //     ...prevState.personalList,
    //     [number] : {
    //       name: name,
    //       completion: 'finish'
    //     }
    //   }
    // }))

    } else if (ver === 'three') {
        let current = this.state.active.complete
          this.setState(prevState => ({
            personalList: {
              [number] : {
                ...prevState.active,
                started: false,
                finish: false,
                complete: !current
            }
          }
        })
        )
    //     this.setState(prevState => ({
    //   personalList : {
    //     ...prevState.personalList,
    //     [number] : {
    //       name: name,
    //       completion: 'complete'
    //     }
    //   }
    // }))
    }        
  }


  // componentDidMount() {
  //   fetch('https://id.twitch.tv/oauth2/token?client_id=oqk1xu4awy8nnm5abfxr8ap86lj1pt&client_secret=ir0i9tsgk0xzobbbngirr1ve0x3xva&grant_type=client_credentials', {method: 'post'})
  //       .then(res => res.json())
  //       .then(data => {
  //         this.setState({authorization: data.access_token})
  //       })

  //   fetch('https://api.igdb.com/v4/games', {
  //             method: 'post',
  //             mode: 'no-cors',
  //             header: { 'Client ID' : this.state.clientId,
  //                       'Authorization' : `Bearer ${this.state.authorization}`},
  //             body: ''
  //           })
  //           .then(response => response.json())
  //           .then(data => console.log(data))    
  // }
   onChange = (e) => {
    this.setState({search: e.target.value})
   }

   onSearch = () => {
      const item = this.state.search
      if( !this.state.log.includes(item)) {
          if(this.state.search.length > 0) {
          if(this.state.log.length > 0) {
            this.setState(prevState => ({
              log: [...prevState.log, item],
              personalList: {...prevState.personalList,
                 [this.state.log.length + 1] : {
                  name: item, 
                  completion: '',
                  active: {
                    started: false,
                    finish: false,
                    complete: false
                  }
                }}
            }))
          } else {
            this.setState({log: [item]})
            this.setState({personalList: {0 : {
              name: item, 
              completion: '',
              active: {
                started: false,
                finish: false,
                complete: false
              }
            }}})
          }  
        }      
      }

      this.setState({search: ''})
      document.querySelector('#search').value = ''
      console.log(this.state.personalList, this.state.log);

    }



  render() { 
    return (
      <div className='App'>
        <Search onChange={this.onChange} onSearch={this.onSearch}/>
        <Main active={this.state.active} onActive={this.onActive} items={this.state.log}/>
      </div>

    );
  }
}

export default App;
