        },
        graphSeg: {
            started: newGraph.started,
            finish: newGraph.finish,
            complete: newGraph.complete
          }
      }))
    } else if(this.state.personalList[number].completion === 'finish') {
        let newGraph = {
           started: currentStart,
           finish: currentFinish - 1,
           complete: currentComplete + 1
          }               
        this.setState(prevState => ({
        personalList : {
          ...prevState.personalList,
          [number] : {
            name: name,
            completion: newItem.completion
          }        
        },
        graphSeg: {
            started: newGraph.started,
            finish: newGraph.finish,
            complete: newGraph.complete
          }
      }))
    }  
    }      
  }

  render() { 
    return (
      <div className='App'>
        <Search onChange={this.onChange} onSearch={this.onSearch}/>
        <div className='main-cont'>
          <Main pers={this.state.personalList} onActive={this.onActive} createGraph={this.createGraph} items={this.state.log}/>
          <Graph graphSeg={this.state.graphSeg} />
        </div>

      </div>

    );
  }
}

export default App;
