import React, { Component } from 'react';
import Search from './components/Search';
import Main from './components/Main';
import Graph from './components/Graph';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import games from './games'



class App extends Component {
  constructor() {
    super()
    this.state = { 
    games : games,
    search: '',
    log: [],
    personalList: {},
    graphSeg: {
    }    
   } 
  }

   onChange = (e) => {
    this.setState({search: e.target.value})
   }

   onSearch = () => {
      const item = this.state.search
      if( !this.state.log.includes(item)) {
          if(this.state.search.length > 0) {
          if(this.state.log.length > 0) {
            this.setState(prevState => ({
              log: [...prevState.log, item],
              personalList: {...prevState.personalList,
                 [this.state.log.length] : {
                  name: item, 
                  completion: ''
                }}
            }))
          } else {
            this.setState({log: [item]})
            this.setState({personalList: {0 : {
              name: item, 
              completion: ''
            }}})
          }  
        }      
      }

      this.setState({search: ''})
      document.querySelector('#search').value = ''
      // console.log(this.state.personalList);      
    }

  onActive = (e, ver) => {

    let name = e.target.parentNode.previousSibling.innerText
    let number = this.state.log.indexOf(name)

    if(ver === 'one') {            
      this.setState(prevState => ({
      personalList : {
        ...prevState.personalList,
        [number] : {
          name: name,
          completion: 'started'
        }
      }
    }))

    } else if (ver === 'two') {      
      this.setState(prevState => ({
      personalList : {
        ...prevState.personalList,
        [number] : {
          name: name,
          completion: 'finish'
        }
      }
    }))

    } else if (ver === 'three') {        
        this.setState(prevState => ({
      personalList : {
        ...prevState.personalList,
        [number] : {
          name: name,
          completion: 'complete'
        }
      }
    }))
    }      
    this.createGraph()  
  }

  createGraph = () => {
    let log = this.state.log
    this.setState({graphSeg: {
          started: 0,
          finish: 0,
          complete: 0
        }    
        })

    log.forEach(item => {




      let number = this.state.log.indexOf(item)
      let completion = this.state.personalList[number].completion

      completion === 'started' ? this.setState(prevState => ({graphSeg : { ...prevState.graphSeg, started: prevState.graphSeg.started + 1}})) : 
        completion === 'finish' ? this.setState(prevState => ({graphSeg : { ...prevState.graphSeg, finish: prevState.graphSeg.finish + 1}})) :
          completion === 'complete' ? this.setState(prevState => ({graphSeg : { ...prevState.graphSeg, complete: prevState.graphSeg.complete + 1}})) :
          console.log('none');

    })

    console.log(this.state.graphSeg);
  }


  // componentDidMount() {
  //   fetch('https://id.twitch.tv/oauth2/token?client_id=oqk1xu4awy8nnm5abfxr8ap86lj1pt&client_secret=ir0i9tsgk0xzobbbngirr1ve0x3xva&grant_type=client_credentials', {method: 'post'})
  //       .then(res => res.json())
  //       .then(data => {
  //         this.setState({authorization: data.access_token})
  //       })

  //   fetch('https://api.igdb.com/v4/games', {
  //             method: 'post',
  //             mode: 'no-cors',
  //             header: { 'Client ID' : this.state.clientId,
  //                       'Authorization' : `Bearer ${this.state.authorization}`},
  //             body: ''
  //           })
  //           .then(response => response.json())
  //           .then(data => console.log(data))    
  // }




  render() { 
    return (
      <div className='App'>
        <Search onChange={this.onChange} onSearch={this.onSearch}/>
        <Main pers={this.state.personalList} onActive={this.onActive} items={this.state.log}/>
        <Graph graphSeg={this.state.graphSeg} />
      </div>

    );
  }
}

export default App;
